import React, { useState } from 'react';

const ProductCard = ({ product, addToCart }) => {
  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setQuantity(value);
    }
  };

  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1);
  };

  return (
    <div className="product-card" style={{ height: '580px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between', border: '1px solid #eee', borderRadius: '10px', padding: '15px', boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)', backgroundColor: '#fff', transition: 'transform 0.3s ease' }}>
      <div className="product-image">
        <img src={product.image} alt={product.name} style={{ height: '280px', objectFit: 'contain', width: '100%', borderRadius: '6px' }} />
      </div>

      <div className="product-info" style={{ padding: '10px 0', textAlign: 'center' }}>
        <h3 className="product-name">{product.name}</h3>
        <p className="product-description">{product.shortDescription}</p>
        <div className="product-price">${product.price.toLocaleString()}</div>
      </div>

      <div className="product-actions" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: 'auto' }}>
        <div className="quantity-selector">
          <label htmlFor={`qty-${product.id}`}>Qty:</label>
          <input
            id={`qty-${product.id}`}
            type="number"
            min="1"
            value={quantity}
            onChange={handleQuantityChange}
          />
        </div>

        <button className="add-to-cart-btn" onClick={handleAddToCart}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
