// src/components/Navbar.js
// This component renders the top navigation bar with links and a shopping cart icon showing item count

import React from 'react';
import { Link } from 'react-router-dom';

// Functional Navbar component with cart count as prop
const Navbar = ({ cartCount }) => {
  // Inline style definitions for layout and design
  const styles = {
    navbar: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '20px 40px',
      backgroundColor: '#fff',
      borderBottom: '1px solid #ddd',
      fontFamily: 'Segoe UI, sans-serif',
    },
    logoText: {
      fontSize: '1.6rem',
      fontWeight: 'bold',
      color: '#9b734c',
      textDecoration: 'none',
    },
    linksList: {
      listStyle: 'none',
      display: 'flex',
      gap: '30px',
      margin: 0,
      padding: 0,
    },
    link: {
      textDecoration: 'none',
      color: '#333',
      fontSize: '1rem',
    },
    cartWrapper: {
      position: 'relative',
    },
    cartIcon: {
      fontSize: '1.4rem',
      textDecoration: 'none',
      color: '#333',
      position: 'relative',
    },
    badge: {
      position: 'absolute',
      top: '-10px',
      right: '-10px',
      backgroundColor: '#9b734c',
      color: 'white',
      fontSize: '0.7rem',
      padding: '3px 6px',
      borderRadius: '50%',
    },
  };

  return (
    <nav style={styles.navbar}>
      {/* Logo section */}
      <div className="navbar-logo">
        <Link to="/" style={styles.logoText}>
          ‚è±Ô∏è WatchHaven
        </Link>
      </div>

      {/* Navigation links */}
      <ul style={styles.linksList}>
        <li><Link to="/" style={styles.link}>Home</Link></li>
        <li><Link to="/luxury-watches" style={styles.link}>Luxury Watches</Link></li>
        <li><Link to="/smart-watches" style={styles.link}>Smart Watches</Link></li>
      </ul>

      {/* Cart icon with item count badge */}
      <div style={styles.cartWrapper}>
        <Link to="/cart" style={styles.cartIcon}>
          üõí
          {/* Show badge only if items are in cart */}
          {cartCount > 0 && <span style={styles.badge}>{cartCount}</span>}
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
