import React, { useState } from 'react';

// Enhanced Product Card component with proper image handling
const ProductCard = ({ product, addToCart }) => {
  const [quantity, setQuantity] = useState(1);
  const [imageError, setImageError] = useState(false);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setQuantity(value);
    }
  };

  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1); // Reset quantity after adding to cart
  };

  // Function to handle image loading errors
  const handleImageError = () => {
    setImageError(true);
  };

  // Default placeholder dimensions
  const placeholderWidth = 300;
  const placeholderHeight = 300;

  return (
    <div className="rounded-lg shadow-md overflow-hidden bg-white hover:shadow-xl transition-shadow duration-300">
      <div className="relative h-64 overflow-hidden bg-gray-100">
        {!imageError ? (
          <img 
            src={product.image} 
            alt={product.name}
            className="w-full h-full object-cover object-center"
            onError={handleImageError}
          />
        ) : (
          <div className="flex flex-col items-center justify-center h-full p-4 bg-gray-200">
            <img 
              src={`/api/placeholder/${placeholderWidth}/${placeholderHeight}`} 
              alt={`${product.name} placeholder`}
              className="max-w-full max-h-full object-contain"
            />
            <span className="mt-2 text-gray-600 text-sm">{product.name}</span>
          </div>
        )}
      </div>

      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-3">{product.shortDescription}</p>
        <div className="text-xl font-bold text-gray-900 mb-4">${product.price.toLocaleString()}</div>

        <div className="flex items-center justify-between">
          <div className="flex items-center border rounded overflow-hidden">
            <label htmlFor={`qty-${product.id}`} className="sr-only">Quantity</label>
            <span className="px-3 py-1 bg-gray-100">Qty:</span>
            <input
              id={`qty-${product.id}`}
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
              className="w-16 py-1 px-2 border-none focus:ring-0"
            />
          </div>

          <button 
            className="bg-amber-700 hover:bg-amber-800 text-white py-2 px-4 rounded transition-colors duration-300"
            onClick={handleAddToCart}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
