// ProductCard.js
// This component displays an individual product card with quantity selector and add-to-cart functionality

import React, { useState } from 'react';

const ProductCard = ({ product, addToCart }) => {
  // Local state to manage the selected quantity of the product
  const [quantity, setQuantity] = useState(1);

  // Handle changes to the quantity input field
  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setQuantity(value);
    }
  };

  // When user clicks "Add to Cart", send product and quantity to parent handler
  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1); // Reset quantity after adding
  };

  return (
    <div className="product-card vertical-card">
      {/* Product image wrapper */}
      <div className="product-image-frame">
        <img
          className="product-image-full"
          src={product.image}
          alt={product.name}
        />
      </div>

      {/* Product name, description, and price */}
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <p className="product-description">{product.shortDescription}</p>
        <div className="product-price">${product.price.toLocaleString()}</div>
      </div>

      {/* Quantity selector and Add to Cart button */}
      <div className="product-actions">
        <div className="quantity-selector">
          <label htmlFor={`qty-${product.id}`}>Qty:</label>
          <input
            id={`qty-${product.id}`}
            type="number"
            min="1"
            value={quantity}
            onChange={handleQuantityChange}
          />
        </div>

        <button className="add-to-cart-btn" onClick={handleAddToCart}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
