import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigation from './components/Navigation';
import Home from './pages/Home';
import ClassicWatches from './pages/ClassicWatches';
import SportWatches from './pages/SportWatches';
import LuxuryWatches from './pages/LuxuryWatches';
import Cart from './components/Cart';
import Footer from './components/Footer';
import './App.css';

function App() {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);

    if (existingItem) {
      setCartItems(cartItems.map(item => 
        item.id === product.id 
          ? { ...item, quantity: item.quantity + product.quantity } 
          : item
      ));
    } else {
      setCartItems([...cartItems, product]);
    }
  };

  return (
    <Router>
      <div className="App">
        <Navigation cartItems={cartItems} />
        <div className="content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route 
              path="/classic" 
              element={<ClassicWatches addToCart={addToCart} />} 
            />
            <Route 
              path="/sport" 
              element={<SportWatches addToCart={addToCart} />} 
            />
            <Route 
              path="/luxury" 
              element={<LuxuryWatches addToCart={addToCart} />} 
            />
            <Route 
              path="/cart" 
              element={<Cart cartItems={cartItems} setCartItems={setCartItems} />} 
            />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
