import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
// Remove lucide-react import and replace with a simple cart icon

const Navbar = ({ cartItemCount }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav className={`navbar ${scrolled ? 'navbar-scrolled' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          <span className="logo-text">LE MONDE DES</span>
          <span className="logo-gold">MONTRES</span>
        </Link>

        <div className="menu-icon" onClick={toggleMenu}>
          <div className={menuOpen ? 'hamburger open' : 'hamburger'}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>

        <ul className={menuOpen ? 'nav-menu active' : 'nav-menu'}>
          <li className="nav-item">
            <Link to="/" className="nav-link" onClick={() => setMenuOpen(false)}>
              Home
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/luxury-watches" className="nav-link" onClick={() => setMenuOpen(false)}>
              Luxury Watches
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/smart-watches" className="nav-link" onClick={() => setMenuOpen(false)}>
              Smart Watches
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/vintage-watches" className="nav-link" onClick={() => setMenuOpen(false)}>
              Vintage Watches
            </Link>
          </li>
        </ul>

        <Link to="/cart" className="cart-icon">
          {/* Replace ShoppingCart component with a simple cart icon */}
          <span role="img" aria-label="Shopping Cart">ðŸ›’</span>
          {cartItemCount > 0 && <span className="cart-count">{cartItemCount}</span>}
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
