// src/pages/SmartWatches.js
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import ProductCard from '../components/ProductCard';

// Import smart watch images
import SL1 from '../images/SL1.jpeg';
import SL2 from '../images/SL2.jpeg';
import SL3 from '../images/SL3.jpeg';

const SmartWatches = ({ watchesData, addToCart }) => {
  const [sortOption, setSortOption] = useState('default');
  const [currentPage, setCurrentPage] = useState(1);
  const [paginatedProducts, setPaginatedProducts] = useState([]);
  const productsPerPage = 3; // Changed to display only 3 watches per page

  // Create sample smart watches data if none provided
  const smartWatchesData = useMemo(() => [
    {
      id: 101,
      name: "TechPro Series 5",
      shortDescription: "Premium smartwatch with advanced health monitoring",
      description: "The TechPro Series 5 combines elegant design with cutting-edge technology. Track your fitness goals, monitor your health, and stay connected with this premium smartwatch.",
      price: 349,
      image: SL1,
      features: ["AMOLED display", "Heart rate monitoring", "Sleep tracking", "7-day battery life", "GPS tracking"]
    },
    {
      id: 102,
      name: "FitSense Ultra",
      shortDescription: "Fitness-focused smartwatch with 30+ activity modes",
      description: "Designed for fitness enthusiasts, the FitSense Ultra offers comprehensive activity tracking with over 30 workout modes, blood oxygen monitoring, and stress tracking in a durable, water-resistant design.",
      price: 229,
      image: SL2,
      features: ["30+ fitness modes", "Blood oxygen monitoring", "Stress tracking", "Water-resistant to 50m", "5-day battery life"]
    },
    {
      id: 103,
      name: "SmartConnect Pro",
      shortDescription: "Premium connected smartwatch with cellular capabilities",
      description: "Stay connected wherever you go with the SmartConnect Pro. With built-in cellular connectivity, you can make calls, send messages, and stream music without your phone.",
      price: 399,
      image: SL3,
      features: ["Cellular connectivity", "Voice assistant", "Music streaming", "NFC payments", "3-day battery life"]
    }
  ], []);

  // Sort products based on selected option
  const getSortedProducts = useCallback(() => {
    const dataToSort = watchesData || smartWatchesData;

    switch(sortOption) {
      case 'price-low':
        return [...dataToSort].sort((a, b) => a.price - b.price);
      case 'price-high':
        return [...dataToSort].sort((a, b) => b.price - a.price);
      case 'name':
        return [...dataToSort].sort((a, b) => a.name.localeCompare(b.name));
      default:
        return dataToSort;
    }
  }, [watchesData, smartWatchesData, sortOption]);

  // Handle pagination
  useEffect(() => {
    const sortedProducts = getSortedProducts();
    const startIndex = (currentPage - 1) * productsPerPage;
    const endIndex = startIndex + productsPerPage;
    setPaginatedProducts(sortedProducts.slice(startIndex, endIndex));
  }, [currentPage, sortOption, watchesData, getSortedProducts]);

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
    setCurrentPage(1); // Reset to first page when sorting changes
  };

  const totalProducts = watchesData ? watchesData.length : smartWatchesData.length;
  const totalPages = Math.ceil(totalProducts / productsPerPage);

  return (
    <div className="category-page">
      <div className="category-header">
        <div className="category-banner" style={{ backgroundImage: `url(${SL1})` }}>
          <h1>Smart Watches</h1>
          <p>Innovation meets style with cutting-edge technology</p>
        </div>
      </div>

      <div className="category-controls">
        <div className="product-count">
          <span>{totalProducts} products</span>
        </div>
        <div className="sort-options">
          <label htmlFor="sort">Sort by:</label>
          <select 
            id="sort" 
            value={sortOption} 
            onChange={handleSortChange}
          >
            <option value="default">Featured</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="name">Name: A to Z</option>
          </select>
        </div>
      </div>

      {/* Adjusted the grid to better accommodate 3 products */}
      <div className="products-grid grid-cols-3">
        {paginatedProducts.map(product => (
          <ProductCard 
            key={product.id} 
            product={product} 
            addToCart={addToCart}
          />
        ))}
      </div>

      {/* Pagination controls */}
      {totalPages > 1 && (
        <div className="pagination">
          <button 
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="pagination-btn"
          >
            Previous
          </button>

          <span className="page-indicator">
            Page {currentPage} of {totalPages}
          </span>

          <button 
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="pagination-btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
};

export default SmartWatches;
