import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import LuxuryWatches from './pages/LuxuryWatches';
import SmartWatches from './pages/SmartWatches';
import VintageWatches from './pages/VintageWatches';
import Cart from './pages/Cart';
import Footer from './components/Footer';

function App() {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product, quantity) => {
    // Check if product is already in cart
    const existingItem = cartItems.find(item => item.id === product.id);

    if (existingItem) {
      // Update quantity if product already exists
      setCartItems(
        cartItems.map(item => 
          item.id === product.id 
            ? { ...item, quantity: item.quantity + quantity } 
            : item
        )
      );
    } else {
      // Add new product to cart
      setCartItems([...cartItems, { ...product, quantity }]);
    }
  };

  const removeFromCart = (productId) => {
    setCartItems(cartItems.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
    } else {
      setCartItems(
        cartItems.map(item => 
          item.id === productId 
            ? { ...item, quantity: newQuantity } 
            : item
        )
      );
    }
  };

  return (
    <Router>
      <div className="App">
        <Navbar cartItemCount={cartItems.reduce((total, item) => total + item.quantity, 0)} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route 
            path="/luxury-watches" 
            element={<LuxuryWatches addToCart={addToCart} />} 
          />
          <Route 
            path="/smart-watches" 
            element={<SmartWatches addToCart={addToCart} />} 
          />
          <Route 
            path="/vintage-watches" 
            element={<VintageWatches addToCart={addToCart} />} 
          />
          <Route 
            path="/cart" 
            element={
              <Cart 
                cartItems={cartItems} 
                removeFromCart={removeFromCart}
                updateQuantity={updateQuantity}
              />
            } 
          />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
