// src/components/ProductCard.js
import React, { useState } from 'react';
import ModalWindow from './ModalWindow'; // Adjust if the path differs

const ProductCard = ({ product, addToCart }) => {
  const [showModal, setShowModal] = useState(false);
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1);
    setShowModal(false); // Close modal after adding
  };

  const handleCardClick = (e) => {
    // Prevent click on quantity or button from opening modal
    if (e.target.tagName !== 'INPUT' && e.target.tagName !== 'BUTTON') {
      setShowModal(true);
    }
  };

  return (
    <>
      <div
        className="product-card vertical-card"
        onClick={handleCardClick}
        style={{ cursor: 'pointer' }}
      >
        <div className="product-image-frame">
          <img className="product-image-full" src={product.image} alt={product.name} />
        </div>

        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          <p className="product-description">{product.shortDescription}</p>
          <div className="product-price">${product.price.toLocaleString()}</div>
        </div>

        <div className="product-actions">
          <div className="quantity-selector">
            <label htmlFor={`qty-${product.id}`}>Qty:</label>
            <input
              id={`qty-${product.id}`}
              type="number"
              min="1"
              value={quantity}
              onChange={(e) => {
                const val = parseInt(e.target.value);
                if (!isNaN(val) && val > 0) {
                  setQuantity(val);
                }
              }}
              onClick={(e) => e.stopPropagation()} // Prevent modal from opening
            />
          </div>

          <button
            className="add-to-cart-btn"
            onClick={(e) => {
              e.stopPropagation(); // Prevent modal from opening
              handleAddToCart();
            }}
          >
            Add to Cart
          </button>
        </div>
      </div>

      {showModal && (
        <ModalWindow
          product={product}
          onClose={() => setShowModal(false)}
          addToCart={handleAddToCart}
        />
      )}
    </>
  );
};

export default ProductCard;
