// src/pages/LuxuryWatches.js
import React, { useState, useEffect, useCallback } from 'react';
import ProductCard from '../components/ProductCard';

import HL1 from '../assets/images/HL1.jpeg';
import HL2 from '../assets/images/HL2.jpeg';
import HL3 from '../assets/images/HL3.jpeg';

const luxuryWatchesData = [
  {
    id: 1,
    name: 'Rolex Classic',
    shortDescription: 'A timeless piece of Swiss luxury.',
    price: 15000,
    image: HL1,
  },
  {
    id: 2,
    name: 'Omega Prestige',
    shortDescription: 'Elegance and performance combined.',
    price: 12000,
    image: HL2,
  },
  {
    id: 3,
    name: 'Cartier Elegance',
    shortDescription: 'Sophistication in every detail.',
    price: 18000,
    image: HL3,
  },
];

const LuxuryWatches = ({ addToCart }) => {
  const [sortOption, setSortOption] = useState('default');
  const [currentPage, setCurrentPage] = useState(1);
  const [paginatedProducts, setPaginatedProducts] = useState([]);
  const productsPerPage = 3;

  const getSortedProducts = useCallback(() => {
    switch (sortOption) {
      case 'price-low':
        return [...luxuryWatchesData].sort((a, b) => a.price - b.price);
      case 'price-high':
        return [...luxuryWatchesData].sort((a, b) => b.price - a.price);
      case 'name':
        return [...luxuryWatchesData].sort((a, b) => a.name.localeCompare(b.name));
      default:
        return luxuryWatchesData;
    }
  }, [sortOption]);

  useEffect(() => {
    const sorted = getSortedProducts();
    const startIndex = (currentPage - 1) * productsPerPage;
    setPaginatedProducts(sorted.slice(startIndex, startIndex + productsPerPage));
  }, [currentPage, sortOption, getSortedProducts]);

  const totalPages = Math.ceil(luxuryWatchesData.length / productsPerPage);

  return (
    <div className="category-page">
      <div className="category-header">
        <div className="category-banner" style={{ backgroundImage: `url(/api/placeholder/1200/400)` }}>
          <h1>Luxury Watches</h1>
          <p>Timeless elegance and precision craftsmanship</p>
        </div>
      </div>

      <div className="category-controls">
        <div className="product-count">
          <span>{luxuryWatchesData.length} products</span>
        </div>
        <div className="sort-options">
          <label htmlFor="sort">Sort by:</label>
          <select id="sort" value={sortOption} onChange={(e) => { setSortOption(e.target.value); setCurrentPage(1); }}>
            <option value="default">Featured</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="name">Name: A to Z</option>
          </select>
        </div>
      </div>

      <div className="products-grid grid-cols-3">
        {paginatedProducts.map(product => (
          <ProductCard key={product.id} product={product} addToCart={addToCart} />
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination">
          <button onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} disabled={currentPage === 1}>Previous</button>
          <span>Page {currentPage} of {totalPages}</span>
          <button onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))} disabled={currentPage === totalPages}>Next</button>
        </div>
      )}
    </div>
  );
};

export default LuxuryWatches;
