import React, { useState } from 'react';

const ProductCard = ({ product, addToCart }) => {
  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setQuantity(value);
    }
  };

  const handleAddToCart = () => {
    addToCart(product, quantity);
    setQuantity(1); // Reset quantity after adding to cart
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300 max-w-sm mx-auto">
      {/* Large Product Image */}
      <div className="relative h-80 bg-gray-50 flex items-center justify-center p-4">
        <img 
          src={product.image} 
          alt={product.name}
          className="max-w-full max-h-full object-contain"
          style={{ maxHeight: '300px', maxWidth: '300px' }}
        />
      </div>

      {/* Product Information */}
      <div className="p-6">
        <h3 className="text-xl font-bold text-gray-900 mb-2">{product.name}</h3>
        <p className="text-gray-600 text-sm mb-4 leading-relaxed">{product.shortDescription}</p>

        {/* Price */}
        <div className="text-2xl font-bold text-gray-900 mb-4">
          ${product.price.toLocaleString()}
        </div>

        {/* Quantity and Add to Cart */}
        <div className="flex items-center gap-3">
          <div className="flex items-center">
            <label htmlFor={`qty-${product.id}`} className="text-sm font-medium text-gray-700 mr-2">
              Qty:
            </label>
            <input
              id={`qty-${product.id}`}
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
              className="w-16 px-2 py-1 border border-gray-300 rounded text-center focus:ring-2 focus:ring-amber-500 focus:border-amber-500"
            />
          </div>

          <button 
            className="flex-1 bg-amber-700 hover:bg-amber-800 text-white py-2 px-4 rounded font-medium transition-colors duration-200"
            onClick={handleAddToCart}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
