import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';

const LuxuryWatches = ({ addToCart }) => {
  const [sortOption, setSortOption] = useState('default');

  // Sort products based on selected option
  const getSortedProducts = () => {
    switch(sortOption) {
      case 'price-low':
        return [...luxuryWatchesData].sort((a, b) => a.price - b.price);
      case 'price-high':
        return [...luxuryWatchesData].sort((a, b) => b.price - a.price);
      case 'name':
        return [...luxuryWatchesData].sort((a, b) => a.name.localeCompare(b.name));
      default:
        return luxuryWatchesData;
    }
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  return (
    <div className="category-page">
      <div className="category-header">
        <div className="category-banner" style={{ backgroundImage: `url(/api/placeholder/1200/400)` }}>
          <h1>Luxury Watches</h1>
          <p>Timeless elegance and precision craftsmanship</p>
        </div>
      </div>

      <div className="category-controls">
        <div className="product-count">
          <span>{luxuryWatchesData.length} products</span>
        </div>
        <div className="sort-options">
          <label htmlFor="sort">Sort by:</label>
          <select 
            id="sort" 
            value={sortOption} 
            onChange={handleSortChange}
          >
            <option value="default">Featured</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="name">Name: A to Z</option>
          </select>
        </div>
      </div>

      <div className="products-grid">
        {getSortedProducts().map(product => (
          <ProductCard 
            key={product.id} 
            product={product} 
            addToCart={(product, quantity) => addToCart(product, quantity)}
          />
        ))}
      </div>
    </div>
  );
};

export default LuxuryWatches;
