// src/App.js

// Import core React tools and state management
import React, { useState } from 'react';

// Import React Router for navigation and routing
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Import shared layout components
import Navbar from './components/Navbar';
import Footer from './components/Footer';

// Import page components
import Home from './pages/Home';
import LuxuryWatches from './pages/LuxuryWatches';
import SmartWatches from './pages/SmartWatches';
import Cart from './pages/Cart';
import ThankYou from './pages/ThankYou';

function App() {
  // Track cart items in state (array of objects)
  const [cartItems, setCartItems] = useState([]);

  /**
   * Add a product to the cart.
   * If it already exists, increase quantity.
   */
  const addToCart = (product, quantity = 1) => {
    setCartItems((prevCartItems) => {
      const existingItem = prevCartItems.find((item) => item.id === product.id);
      if (existingItem) {
        return prevCartItems.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        return [...prevCartItems, { ...product, quantity }];
      }
    });
  };

  /**
   * Update quantity of a specific item in the cart.
   */
  const updateQuantity = (productId, newQuantity) => {
    setCartItems((prevCartItems) =>
      prevCartItems.map((item) =>
        item.id === productId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  /**
   * Clear all items from the cart (used on Thank You page).
   */
  const clearCart = () => {
    setCartItems([]);
  };

  // Calculate total number of items in cart
  const totalCartCount = cartItems.reduce((total, item) => total + item.quantity, 0);

  return (
    <Router>
      {/* Global navigation bar with live cart count */}
      <Navbar cartCount={totalCartCount} />

      {/* Page routing definitions */}
      <Routes>
        <Route path="/" element={<Home />} />

        <Route
          path="/luxury-watches"
          element={<LuxuryWatches addToCart={addToCart} />}
        />

        <Route
          path="/smart-watches"
          element={<SmartWatches addToCart={addToCart} />}
        />

        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cartItems}
              updateQuantity={updateQuantity}
              clearCart={clearCart}
            />
          }
        />

        <Route path="/thank-you" element={<ThankYou />} />
      </Routes>

      {/* Global footer at bottom of every page */}
      <Footer />
    </Router>
  );
}

export default App;
