// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import './App.css';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import LuxuryWatches from './pages/LuxuryWatches';
import SmartWatches from './pages/SmartWatches';
import Cart from './pages/Cart';

// Sample watch data
const luxuryWatchesData = [
  {
    id: 1,
    name: "Royal Oak Chronograph",
    price: 24500,
    image: "/api/placeholder/400/400",
    shortDescription: "Iconic octagonal bezel with 'tapisserie' dial pattern",
    description: "The Royal Oak Chronograph continues to be one of the most sought-after luxury sport watches. Its distinctive octagonal bezel and integrated bracelet design have made it an icon in the world of horology.",
    features: [
      "Self-winding chronograph movement",
      "41mm stainless steel case",
      "Water resistant to 50m",
      "Sapphire crystal case back",
      "Blue 'Grande Tapisserie' dial"
    ]
  },
  {
    id: 2,
    name: "Nautilus Automatic",
    price: 35000,
    image: "/api/placeholder/400/400",
    shortDescription: "Elegant sport watch with horizontally embossed dial",
    description: "The Nautilus has remained virtually unchanged since its introduction in 1976. Its porthole-inspired case design and horizontally embossed dial make it one of the most recognizable luxury watches in the world.",
    features: [
      "Self-winding mechanical movement",
      "40mm stainless steel case",
      "Water resistant to 120m",
      "Date display at 3 o'clock",
      "Blue-black gradient dial"
    ]
  },
  {
    id: 3,
    name: "Submariner Date",
    price: 15750,
    image: "/api/placeholder/400/400",
    shortDescription: "The quintessential divers' watch",
    description: "The Submariner Date is the reference among divers' watches. Introduced in 1953, it was the first watch to be water-resistant to 100 meters. Its unidirectional rotating bezel allows divers to measure immersion time with certainty.",
    features: [
      "Perpetual self-winding mechanical movement",
      "41mm Oystersteel case",
      "Water resistant to 300m",
      "Cerachrom ceramic bezel insert",
      "Chromalight display for legibility in dark conditions"
    ]
  },
  {
    id: 4,
    name: "Daytona Cosmograph",
    price: 32000,
    image: "/api/placeholder/400/400",
    shortDescription: "Legendary chronograph designed for racing drivers",
    description: "The Cosmograph Daytona was designed to meet the demands of professional racing drivers. With its tachymetric scale and chronograph functions, it allows drivers to measure average speeds up to 400 kilometers or miles per hour.",
    features: [
      "Mechanical chronograph movement with column wheel",
      "40mm 18k gold case",
      "Water resistant to 100m",
      "Fixed bezel with tachymetric scale",
      "Small seconds display at 6 o'clock"
    ]
  }
];

const smartWatchesData = [
  {
    id: 101,
    name: "Ultimate Pro 5",
    price: 799,
    image: "/api/placeholder/400/400",
    shortDescription: "Advanced health monitoring with premium design",
    description: "The Ultimate Pro 5 combines sophisticated health sensors with a premium titanium design. Monitor your heart rate, blood oxygen, ECG, sleep patterns, and more in a lightweight, durable package.",
    features: [
      "Always-on OLED display",
      "Titanium case with sapphire crystal",
      "Advanced health monitoring suite",
      "5-day battery life",
      "5ATM water resistance"
    ]
  },
  {
    id: 102,
    name: "Connect Series 8",
    price: 499,
    image: "/api/placeholder/400/400",
    shortDescription: "Seamless connectivity with smart notifications",
    description: "The Connect Series 8 offers the perfect blend of style and technology. With cellular connectivity, you can make calls, send texts, and stream music even when your phone isn't nearby.",
    features: [
      "Retina LTPO OLED display",
      "Aluminum or stainless steel options",
      "Cellular connectivity",
      "18-hour battery life",
      "Crash detection and emergency SOS"
    ]
  },
  {
    id: 103,
    name: "Active Precision",
    price: 349,
    image: "/api/placeholder/400/400",
    shortDescription: "Built for athletes with advanced workout tracking",
    description: "The Active Precision watch is designed specifically for serious athletes. With precision GPS tracking, personalized workout recommendations, and recovery monitoring, it's your ultimate training partner.",
    features: [
      "Multi-band GPS for precise tracking",
      "Training load and recovery metrics",
      "Heart rate variability monitoring",
      "14-day battery life in smartwatch mode",
      "60 hours in GPS mode"
    ]
  }
];

function App() {
  const [cartItems, setCartItems] = useState([]);

  // Load cart from localStorage on initial render
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // Add item to cart function
  const addToCart = (product, quantity = 1) => {
    setCartItems(prevItems => {
      // Check if product is already in cart
      const existingItemIndex = prevItems.findIndex(item => item.id === product.id);

      if (existingItemIndex > -1) {
        // Product exists, update quantity
        const updatedItems = [...prevItems];
        updatedItems[existingItemIndex] = {
          ...updatedItems[existingItemIndex],
          quantity: updatedItems[existingItemIndex].quantity + quantity
        };
        return updatedItems;
      } else {
        // Product doesn't exist in cart, add new item
        return [...prevItems, { ...product, quantity }];
      }
    });
  };

  // Update quantity of an item
  const updateQuantity = (productId, newQuantity) => {
    setCartItems(prevItems => 
      prevItems.map(item => 
        item.id === productId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  // Remove item from cart
  const removeFromCart = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  // Get total number of items in cart
  const cartItemCount = cartItems.reduce((total, item) => total + item.quantity, 0);

  return (
    <Router>
      <div className="App">
        <Navbar cartItemCount={cartItemCount} />

        <Routes>
          <Route path="/" element={<Home />} />
          <Route 
            path="/luxury-watches" 
            element={
              <LuxuryWatches 
                watchesData={luxuryWatchesData} 
                addToCart={addToCart} 
              />
            } 
          />
          <Route 
            path="/smart-watches" 
            element={
              <SmartWatches 
                watchesData={smartWatchesData} 
                addToCart={addToCart} 
              />
            } 
          />
          <Route 
            path="/cart" 
            element={
              <Cart 
                cartItems={cartItems} 
                updateQuantity={updateQuantity} 
                removeFromCart={removeFromCart} 
              />
            } 
          />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
