// src/pages/ThankYou.js

// Import React and React Router's Link for navigation
import React from 'react';
import { Link } from 'react-router-dom';

/**
 * ThankYou component displays a confirmation message after checkout.
 * It includes a thank you note and a link to return to the homepage.
 */
const ThankYou = () => {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üéâ Thank You for Your Purchase!</h1>

      <p style={styles.message}>
        We truly appreciate your business and hope you enjoy your new timepiece.
        If you have any questions, feel free to contact us. We‚Äôre always here to help!
      </p>

      {/* Link back to home for continued shopping */}
      <Link to="/" style={styles.link}>‚Üê Continue Shopping</Link>
    </div>
  );
};

// Inline styles used to control layout and visual presentation
const styles = {
  container: {
    padding: '60px',
    textAlign: 'center',
    fontFamily: 'Segoe UI, sans-serif',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: '20px',
    color: '#9b734c', // Elegant brown-gold
  },
  message: {
    fontSize: '1.2rem',
    marginBottom: '30px',
    color: '#333',
  },
  link: {
    fontSize: '1rem',
    padding: '10px 20px',
    backgroundColor: '#9b734c',
    color: '#fff',
    borderRadius: '5px',
    textDecoration: 'none',
  },
};

export default ThankYou;
